from flask import Flask, render_template
app = Flask(__name__)

@app.route("/")
def index():
    myMovies = ['Family Man','Cobra Kai','While You Were Sleeping', 'John Wick', 'Big Sky']
    fav = ['Cobra Kai']
    pics = ['https://wallpaperaccess.com/full/6042438.jpg', 'https://resizing.flixster.com/ZZgmKKErY7vE9CNpcs-ewae8dwM=/ems.ZW1zLXByZC1hc3NldHMvdHZzZXJpZXMvUlRUVjc1NjkwMS53ZWJw', 'https://m.media-amazon.com/images/I/71MS-o1X7TL._SY606_.jpg', 'data:image/jpeg;base64,', 'https://m.media-amazon.com/images/M/MV5BZTBjY2EwYjQtYjA3MC00YmJhLWI5NzktZjJlNjEyMDQwN2Y5XkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_.jpg']
    return render_template("index.html", fav=fav, num=len(myMovies), movies=myMovies, pics=pics)

if __name__ == '__main__':
    app.run(debug=True)
